async function backup() {
    try {
        const fs = require('fs');
        const date = new Date();
        const files = ["commands.json", "events.json", "players.json", "globalVars.json", "servers.json", "serverVars.json", "settings.json"]

        const dir0 = `./data/Backup`
        const dir1 = `./data/Backup/${date.getFullYear()}-${date.getMonth() + 1}-${date.getDate()}`
        const dir2 = `${dir1}/${Date.now()}`

        if (!fs.existsSync(dir0)) fs.mkdirSync(dir0);
        if (!fs.existsSync(dir1)) fs.mkdirSync(dir1);
        fs.mkdirSync(dir2);

        for (i in files) {
            fs.copyFileSync(`./data/${files[i]}`, `${dir2}/${files[i]}`)
        }
    } catch (error) {
        console.log("An error occured with *BACKUP CREATION*")
    }
}

backup()
